	$ua = $_GET['ua'];
	$requestingDevice = $wurflManager->getDeviceForUserAgent($_GET['ua']);
} else {
	$ua = $_SERVER['HTTP_USER_AGENT'];
	$ip_address = $_SERVER["REMOTE_ADDR"];
	$page_name = $_SERVER["SCRIPT_NAME"];
	$query_string = $_SERVER["QUERY_STRING"];
	$current_page = $page_name."?".$query_string;
	// This line detects the visiting device by looking at its HTTP Request ($_SERVER)
	$requestingDevice = $wurflManager->getDeviceForHttpRequest($_SERVER);
	$rD = $requestingDevice;
}

//$ip = $_REQUEST['REMOTE_ADDR']; // the IP address to query
// .', '.$query['message'].', '.$query['reverse']
$query = @unserialize(file_get_contents('http://ip-api.com/php/'.$ip_address));
if($query && $query['status'] == 'success') {
	$qr = $query;
} else {
	echo 'Unable to get location';
}

